var button=document.querySelector(".search-button"),form=document.querySelector("form"),dateIn=form.querySelector("[id=date-in]"),dateOut=form.querySelector("[id=date-out]"),adults=form.querySelector("[id=adults]"),kids=form.querySelector("[id=kids]"),storage_adults=localStorage.getItem("adults"),storage_kids=localStorage.getItem("kids");button.addEventListener("click",function(e){e.preventDefault(),form.classList.toggle("modal-show"),storage_adults?adults.value=storage_adults:adults.focus(),storage_kids?kids.value=storage_kids:storage_adults?kids.focus():adults.focus()}),form.addEventListener("submit",function(e){dateIn.value&&dateOut.value&&adults.value&&kids.value?(localStorage.setItem("adults",adults.value),localStorage.setItem("kids",kids.value)):(e.preventDefault(),console.log("Нужно заполнить обязательные поля: дата заезда, дата выезда, взрослые, дети!"))});
